mapper.select:
SELECT * FROM ( SELECT b.BOARD_NO, b.BOARD_TITLE, b.BOARD_VIEWS, b.CATEGORY_ID, bc.CATEGORY_NAME, m.USER_NAME AS BOARD_WRITER, TO_CHAR(b.ENROLL_DATE, 'YYYY/MM/DD') AS ENROLL_DATE, ROW_NUMBER() OVER ( ORDER BY
b.BOARD_VIEWS DESC
b.ENROLL_DATE DESC
b.BOARD_NO DESC
) AS RN FROM BOARD b LEFT JOIN BOARD_CATEGORY bc ON b.CATEGORY_ID = bc.CATEGORY_ID LEFT JOIN MEMBER m ON b.USER_NO = m.USER_NO
WHERE b.STATUS = 'Y'
AND b.CATEGORY_ID = #{categoryId}
) subquery WHERE RN BETWEEN #{startRow} AND #{endRow}

mapper.delete:
DELETE FROM
LIKES_TABLE
WHERE COMMENT_NO = #{commentNo}
AND USER_NO = #{userNo}

mapper.select:
SELECT category_name
FROM BOARD_CATEGORY
WHERE category_id = #{categoryId}

mapper.insert:
INSERT INTO FILE_TABLE (
FILE_NO, BOARD_NO, USER_NO, ORIGIN_NAME, CHANGE_NAME, ALLOW_DOWNLOAD,
FILE_SIZE
) VALUES (
SEQ_FILE_NO.NEXTVAL, #{boardNo}, #{userNo},
#{originName}, #{changeName},
CASE WHEN #{allowDownload} IS NULL THEN
'Y' ELSE #{allowDownload} END,
#{fileSize}
)

mapper.update:
UPDATE
FILE_TABLE
SET ALLOW_DOWNLOAD = #{allowDownload}
WHERE FILE_NO =
#{fileNo}

UPDATE FILE_TABLE
SET ALLOW_DOWNLOAD = #{allowDownload}
WHERE BOARD_NO = #{boardNo}
