mapper.select:
SELECT i.INQUIRY_NO AS INQUIRY_NO, i.USER_NO AS USER_NO, m.USER_NAME AS INQUIRY_WRITER, i.CATEGORY_ID AS CATEGORY_ID, CASE WHEN i.CATEGORY_ID = 'CAT06' THEN sc.SUBCATEGORY_NAME ELSE bc.CATEGORY_NAME END AS CATEGORY_NAME, i.INQUIRY_TITLE AS INQUIRY_TITLE, i.INQUIRY_CONTENT AS INQUIRY_CONTENT, i.ADMIN_REPLY AS ADMIN_REPLY, i.STATUS AS STATUS, TO_CHAR(i.ENROLL_DATE, 'YYYY/MM/DD') AS ENROLL_DATE, TO_CHAR(i.UPDATE_DATE, 'YYYY/MM/DD') AS UPDATE_DATE, i.INQUIRY_VIEWS AS INQUIRY_VIEWS, ROW_NUMBER() OVER ( ORDER BY
i.ENROLL_DATE DESC
i.INQUIRY_NO DESC
) AS RN FROM INQUIRY i LEFT JOIN MEMBER m ON i.USER_NO = m.USER_NO LEFT JOIN BOARD_CATEGORY bc ON i.CATEGORY_ID = bc.CATEGORY_ID -- 상위 카테고리 LEFT JOIN SUBCATEGORY sc ON i.SUBCATEGORY_ID = sc.SUBCATEGORY_ID -- 수정된 하위 카테고리 WHERE i.STATUS = 'Y'
AND i.CATEGORY_ID = #{categoryId}

mapper.update:
UPDATE INQUIRY
SET INQUIRY_VIEWS = INQUIRY_VIEWS + 1
WHERE INQUIRY_NO = #{ino}
